        {% for role in app.session.get('user').MemberRole %}
            {% if role.id ==  \"6\" %}
            <a href=\"{{ path('school_stop_edit', {'id': school_stop.id}) }}\">modifier</a>
            <br>
            {{ include('school_stop/_delete_form.html.twig') }}
            {% endif %}
        {% endfor %}
    {% endif %}
   
{% endblock %}", "school_stop/show.html.twig", "/Users/marie-aude/Desktop/Sauvegarde TMK 311018/templates/school_stop/show.html.twig");
