    public function getSourceContext()
    {
        return new Twig_Source("{% extends 'base.html.twig' %}

{% block title %}Connexion{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link rel=\"stylesheet\" href=\"{{ asset('css/login.css') }}\">
{% endblock %}

{% block body %}

{% if error %}
    <div>{{ error.messageKey|trans(error.messageData, 'security') }}</div>
{% endif %}

    <form class=\"form-signin\" action=\"{{ path('connexion') }}\" method=\"post\">
        {% if error %}
            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
        {% endif %}
        <h1 class=\"h3 mb-3 font-weight-normal\">Identifiez-vous</h1>
        <label for=\"inputEmail\" class=\"sr-only\">Adresse email</label>
        <input type=\"email\" name=\"email\" id=\"inputEmail\" class=\"form-control\" placeholder=\"Adresse email\" required autofocus>
        <label for=\"inputPassword\" class=\"sr-only\">Mot de passe</label>
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" placeholder=\"Mot de passe\" required>
        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" value=\"remember-me\"> Se souvenir de moi
            </label>
        </div>
        {#
    If you want to control the URL the user
    is redirected to on success (more details below)
    <input type=\"hidden\" name=\"_target_path\" value=\"/account\" />
    #}
    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">
        <button class=\"btn btn-lg btn-primary btn-block\" type=\"submit\">
            Je m'idenfifie
        </button>
    </form>

{% endblock %}", "security/connexion.html.twig", "/Users/marie-aude/Desktop/TMK/templates/security/connexion.html.twig");
